materials.vertexShader:
varying vec2 vUv;
uniform sampler2D uPerlin;
uniform float uTime;

vec2 rotated2D(vec2 value, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    mat2 m = mat2(c, -s, s, c);
    return m * value;
}

void main() {
    vec3 newPosition = position;
    //random position of the perlin for ROTATION
    float perlin = texture(uPerlin, vec2(0.5, uv.y *0.2 - uTime * 0.005)).r;
    float angle = perlin * 10.0;
    newPosition.xz = rotated2D(position.xz, angle);

    // wind effect
    vec2 windOffset = vec2(
        texture(uPerlin, vec2(0.25, uTime * 0.01)).r -0.5,
        texture(uPerlin, vec2(0.75, uTime * 0.01)).r -0.5
        );
    windOffset.x *= pow(uv.y, 3.0) * 10.0;
    windOffset.y *= pow(uv.y, 3.0) * 10.0;
    newPosition.xz += windOffset;

    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
    vUv = uv;
}

materials.fragmentShader:
varying vec2 vUv;
uniform sampler2D uPerlin;
uniform float uTime;

void main() {
  vec2 smokeUv = vUv;
  smokeUv.x *= 0.5;
  smokeUv.y *= 0.3;
  smokeUv.y -= uTime * 0.03;

  float smoke = texture2D(uPerlin, smokeUv).r;

  //remap
  smoke = smoothstep(0.4, 1.0, smoke);

  //edges
  smoke *= smoothstep(0.0, 0.1, vUv.x);
  smoke *= 1.0 - smoothstep(0.9, 1.0, vUv.x);
  smoke *= smoothstep(0.0, 0.1, vUv.y);
  smoke *= 1.0 - smoothstep(0.4, 1.0, vUv.y);


  gl_FragColor = vec4(0.6, 0.3, 0.2, smoke);
  #include <tonemapping_fragment>
  #include <colorspace_fragment>
}
